import React, { useState } from 'react';

const ApologyForm: React.FC = () => {
  const [formData, setFormData] = useState({
    recipient: '',
    senderName: '',
    senderRole: '',
    reason: '',
    wordCount: '',
    style: '',
  });
  const [apologyImage, setApologyImage] = useState<string | null>(null);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/generate-apology', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      setApologyImage(data.imageUrl);
    } catch (error) {
      console.error('Error generating apology:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="recipient"
          placeholder="道歉对象"
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="senderName"
          placeholder="道歉人姓名"
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="senderRole"
          placeholder="道歉人角色"
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="reason"
          placeholder="道歉原因"
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          name="wordCount"
          placeholder="道歉信字数"
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="style"
          placeholder="道歉风格"
          onChange={handleInputChange}
          required
        />
        <button type="submit">生成道歉信</button>
      </form>
      {apologyImage && (
        <div>
          <img src={apologyImage} alt="道歉信" />
          <a href={apologyImage} download="apology_letter.jpg">
            下载道歉信
          </a>
        </div>
      )}
    </div>
  );
};

export default ApologyForm;
